mapper.select:
SELECT *
FROM cont
WHERE cont_no = #{cont_no}

mapper.update:
UPDATE cont
SET cont_view = cont_view + 1
WHERE cont_no = #{cont_no}

mapper.select:
select c.cont_name,c.cont_no,cont_img,cont_addr1,AA.price as price ,IFNULL(avg(r.review_star),0) as review_avg,count(r.review_no) as review_count from cont c inner join
(select cont_no, min(prod_price) as price from prod group by cont_no union select cont_no, min(one_price) as price from one group by cont_no )
AA on c.cont_no=AA.cont_no left outer join review r on c.cont_no = r.cont_no and review_status!="N" where c.cont_no in
#{item}
group by c.cont_no;

SELECT cate_large, cate_middle
FROM cate
WHERE  cate_large = #{cate_large}
