mapper.select:
select * from (

select zzim.zzim_no as zzim_no, zzim.user_id as user_id, zzim.cont_no as cont_no, A.cont_name as cont_name, A.cont_addr1 as cont_addr1, A.cont_img as cont_img, A.one_date as one_date, A.one_price as price from zzim
inner join (
select cont.cont_no as cont_no, cont_name, cont_addr1, cont_img, one_date, one_price from cont inner join one
on cont.cont_no=one.cont_no
) A
on zzim.cont_no=A.cont_no where user_id=#{user_id}



) AA
inner join review
on AA.cont_no=review.cont_no where AA.user_id=#{user_id} group by AA.cont_no;
